package Tugasp7;

public class HurufZkeA {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		 System.out.println("Huruf dari Z ke A:");
	        char ch = 'Z';
	        do {
	            System.out.print(ch + " ");
	            ch--;
	        } while (ch >= 'A');
	}
}
